const InputWithClean = ({name, setName, surname, setSurname}) => {

    const onChange = (event) => {
        const inputValue = event.target.value;
        if (inputValue.length > 0) {
            setName(event.target.value)
        } else {
            setName('Person');
        }
    }

    return  (
            <div>
                    <input 
                        placeholder='Please enter your name' 
                        onChange = {onChange} 
                    />
                    <button></button>
            </div>
            );
}
