import React, { useState } from 'react';

const NameInput = () => {
    const [name, setName] = useState('Person');

    const onChange = (event) => {
        setName(event.target.value)
    }

    return  (
            <div>
                <h2>Hello, {name.lenght}</h2>
                    <input 
                        placeholder='Please enter your name' 
                        onChange = {onChange} 
                    />
            </div>
            );

}
export default NameInput;
