const InputWithClear = ({name, setName, surname, setSurname, message, setMessage}) => {

    const onClick = () => {
        setMessage('');
    };

    const onChange = event => {
        setMessage(event.target.value);
    };

    return  (
            <div>
                    <input 
                        placeholder='Please enter your name' 
                        onChange = {onChange}
                        value={message}
                    />
                    <button onClick={onClick}>Clear field</button>            
            </div>
            );
}
export default InputWithClear;
