import { useState, useEffect } from 'react';
import './App.css';
import NameInput from './components/NameInput'

function App() {
  useEffect(() => {
    document.title = `Hello, ${name} ${surname}`;
  });

  const [name, setName] = useState('');
  const [surname, setSurname] = useState();

  return (
    <div className="App">
      <h1>Lesson5</h1>
      <NameInput name={name} setValue={setName} placeholder='Please enter your name' />
      <NameInput surname={surname} setValue={setSurname} placeholder='Please enter your surname' />
    </div>
  );
}

export default App;
